<script>
    import { goto } from "$app/navigation"

  



    function createID() {
        let abc = "abcdefghijklmnopqrstuvwxyz"
        let num = "1234567890"
        let id = ""
        for(let i = 0; i < 7; i++) {
            id += Math.floor(Math.random() * 2) == 0? abc[Math.floor(Math.random() * abc.length)] : num[Math.floor(Math.random() * num.length)] 
        }
        return id
    }


    function createRoom() {
        let id = createID()
        goto(`/room/${id}`)
    
    }
    
</script>



